import React, { Fragment, useEffect, useState } from "react";
// import { CgMouse } from "react-icons/all";
import "./Home.css";
import ProductCard from "./ProductCard.js";
import MetaData from "../layout/MetaData";
import { clearErrors, getProduct } from "../../actions/productAction";
import { useSelector, useDispatch } from "react-redux";
import Loader from "../layout/Loader/Loader";
import { useAlert } from "react-alert";

const Home = () => {
  const alert = useAlert();
  const dispatch = useDispatch();
  const { loading, error, products } = useSelector((state) => state.products);
  const [showPopup, setShowPopup] = useState(false);

  useEffect(() => {
    if (error) {
      alert.error(error);
      dispatch(clearErrors());
    }
    dispatch(getProduct());
  }, [dispatch, error, alert]);

  useEffect(() => {
    const popup = document.getElementById('popup');

    const timer = setTimeout(() => {
      setShowPopup(true);
    }, 5000); // Show the popup after 5 seconds

    // Clear the timer when the component unmounts
    return () => clearTimeout(timer);
  }, []);

  const closePopup = () => {
    setShowPopup(false);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      const popup = document.getElementById('popup');
      if (popup && !popup.contains(event.target)) {
        closePopup();
      }
    };

    if (showPopup) {
      document.addEventListener('click', handleClickOutside);
    }

    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, [showPopup]);


  // const popup = document.querySelector('#popup');

  // window.addEventListener('load', () => {
  //   showPopup();
  // })

  // function showPopup() {
  //   const timeLimit = 5;
  //   let i = 0;
  //   const timer = setInterval(() => {
  //     i++;
  //     if (i == timeLimit) {
  //       clearInterval(timer);
  //       popup.classList.add('show');
  //     }
  //     console.log(i);
  //   }, 1000)
  //   window.onclick = (event) => {
  //     if (!event.target.matches('.popup__box')) {
  //       if (popup.classList.contains('show')) {
  //         popup.classList.remove('show');
  //         console.log("remove")
  //       }
  //     }
  //   }
  // }
  return (
    <Fragment>
      {loading ? (
        <Loader />
      ) : (<>

        <div className="popup" id="popup">
          <div>
            <div className="popup__box">
              <div className="popup__box--h1">
                <h1>Love rant sessions?</h1>
                <h1>us too...</h1>
              </div>
              <div className="popup__box--h3">
                <h3>
                  <span className="letter--1">s</span>
                  <span className="letter--2">o</span>
                  <span className="letter--3">w</span>
                  <span className="letter--4">h</span>
                  <span className="letter--5">y</span>
                  <span className="letter--6">n</span>
                  <span className="letter--7">o</span>
                  <span className="letter--8">t</span>
                  <span className="letter--9">d</span>
                  <span className="letter--10">o</span>
                  <span className="letter--11">i</span>
                  <span className="letter--12">t</span>
                  <span className="letter--13">t</span>
                  <span className="letter--14">o</span>
                  <span className="letter--15">g</span>
                  <span className="letter--16">e</span>
                  <span className="letter--17">t</span>
                  <span className="letter--18">h</span>
                  <span className="letter--19">e</span>
                  <span className="letter--20">r</span>
                </h3>
              </div>
              <div className="popup__box--p">
                <p>
                  subscribe to our news letter for weekly rant sessions, exclusive shop
                  updates and (sshh don't tell anyone) discount codes
                </p>
              </div>
              <div>
                <a href="" className="btn popup__box--button">
                  subscribe now
                </a>
              </div>
            </div>
          </div>
        </div>

        <section className="description__main">
          <div>
            <div className="description__img">
              <img src="" alt="" />
            </div>
            <div className="description__intro">
              <div className="shop__categories">
                <div className="shop__categories--left">
                  <h3>shop by category</h3>
                  <ul className="shop__by__category">
                    <li>
                      <a href="./categories/categories.html">scented candles</a>
                    </li>
                    <li>
                      <a href="./categories/categories.html">stickers</a>
                    </li>
                    <li>
                      <a href="./categories/categories.html">notebooks</a>
                    </li>
                    <li>
                      <a href="./categories/categories.html">notepads</a>
                    </li>
                    <li>
                      <a href="./categories/categories.html">art prints</a>
                    </li>
                    <li>
                      <a href="./categories/categories.html">upcoming</a>
                    </li>
                  </ul>
                </div>
                <div className="shop__categories--right">
                  <h3>shop all</h3>
                  <h3>custom gifts</h3>
                  <ul className="custom__gifts">
                    <li>
                      <a href="">build your own basket</a>
                    </li>
                    <li>
                      <a href="">contact us</a>
                    </li>
                    <li>
                      <a href="">bulk orders</a>
                    </li>
                  </ul>
                </div>
              </div>
              <div className="description">
                <h1>welcome pricks!</h1>
                <p className="description__para">
                  you're now visiting a one-stop shop to satisfy our need for some
                  maximalist (or minimalist) dopamine decir art and stationary
                  cravings, the product of years and years of being completely
                  random- we present to you a wholesome collection of <br />
                  <span className="description_collection">
                    • stickers • memo pads • notebooks • notepads • zins • scented
                    candles
                  </span>
