import {
    EMAIL_REQUEST,
    EMAIL_FAIL,
    EMAIL_SUCCESS,
    CLEAR_ERRORS
} from "../constants/emailConstants.js";

export const emailReducer = (state = { emails: [] }, action) => {
    switch (action.type) {
        case EMAIL_REQUEST:
            return {
                ...state,
                loading: true,
            };
        case EMAIL_SUCCESS:
            return {
                ...state,
                loading: false,
                emails: action.payload,
            };

        case EMAIL_FAIL:
            return {
                ...state,
                loading: false,
                error: action.payload,
            };

        case CLEAR_ERRORS:
            return {
                ...state,
                error: null,
            };

        default:
            return state;
    }
};
