import {
    EMAIL_REQUEST,
    EMAIL_FAIL,
    EMAIL_SUCCESS,
    ADMIN_EMAIL_REQUEST,
    ADMIN_EMAIL_SUCCESS,
    ADMIN_EMAIL_FAIL,
    CLEAR_ERRORS
} from "../constants/emailConstants.js";

export const emailReducer = (state = { emails: [] }, action) => {
    switch (action.type) {
        case ADMIN_EMAIL_REQUEST:
            return {
                loading: true,
                emails: [],
            };
        case ADMIN_EMAIL_SUCCESS:
            console.log("Received email data:", action.payload);
            return {
                loading: false,
                emails: action.payload
            };

        // case ADMIN_EMAIL_SUCCESS:
        //     return {
        //         loading: false,
        //         emails: action.payload,
        //     };
        // case EMAIL_FAIL:
        case ADMIN_EMAIL_FAIL:
            return {
                loading: false,
                error: action.payload,
            };

        case CLEAR_ERRORS:
            return {
                ...state,
                error: null,
            };
        default:
            return state;
    }
};
