                  <p>item 1</p>
                  <img src="" alt="" className="cart__item--img" />
                  <div>
                    <p>quantity</p>
                    <p>price</p>
                  </div>
                </div>
                <hr />
                <div className="cart__item">
                  <p>item 1</p>
                  <img src="" alt="" className="cart__item--img" />
                  <div>
                    <p>quantity</p>
                    <p>price</p>
                  </div>
                </div>
                <hr />
                <div className="cart__item">
