                  <div>
                    <p>quantity</p>
                    <p>price</p>
                  </div>
                </div>
                <hr />
                <div className="cart__total">
                  <div>
                    <p>cart total</p>
                    <p>price</p>
                  </div>
                  <a href="" className="btn checkout__btn2">
                    check out
                  </a>
                </div>
              </div>
              <div className="product__cart--nav">
                <a>
                  <i className="fa-solid fa-chevron-left" />
